function peri(a){$(".search-list").html("");var s="";if(a.data.length){$(".search-list").removeClass("h"),$(".search-hot").addClass("h");for(var e=0;e<a.data.length;e++)s+='<a target="_blank" href="#" class="pi"><span class="pi_text">'+a.data[e].keywords+'</span><i class="icon-a-autofill"></i></a>';$(".search-list").append(s)}else $(".search-list").addClass("h"),$(".search-hot").removeClass("h")}$(".search").keyup(function(){if(""!=$(this).val()){var a=document.createElement("script");a.src="http://www.xbiao.com/search/suggestion/?wd="+this.value+"&callback=peri",document.body.append(a)}else $(".search-list").addClass("h"),$(".search-hot").removeClass("h")});