function setCookie(e,t,o){if(null!=o){var i=o,n=new Date;n.setDate(n.getDate()+i),document.cookie=e+"="+escape(t)+";expires="+n.toGMTString()}else document.cookie=e+"="+escape(t)}function getCookie(e){for(var t,o=document.cookie.split("; "),i=0;i<o.length;i++){var n=o[i].split("=");if(e==n[0]){t=unescape(n[1]);break}t=null}return t}function delCookie(e){var t=new Date;t.setTime(t.getTime()-1e4),document.cookie=e+"=;expires="+t.toGMTString()}